import { Literal } from "n3";
/**
 * Extract a timestamp (ms) from an RDF Literal
 * @param dateTimeLiteral
 * @returns {number}
 */
export declare function extractTimestampFromLiteral(dateTimeLiteral: Literal): number;
/**
 * Convert a timestamp (ms) to an RDF Literal
 * @param timestamp
 * @returns {Literal}
 */
export declare function timestampToLiteral(timestamp: number): Literal;
/** Convert a Date object to an RDF Literal
 * @param date
 * @returns {Literal}
 */
export declare function dateToLiteral(date: Date): Literal;
/**
 * Extract a Date object from an RDF Literal
 * @param dateTimeLiteral
 * @returns {Date}
 */
export declare function extractDateFromLiteral(dateTimeLiteral: Literal): Date;
